import React, {useEffect, useState} from "react";


export default function RatingButton(props){
    const [style, setStyle] = useState({});

    useEffect(() => {
        if (props.currentRating === props.rating) {
            setStyle({
                color: "red"
            });
        } else {
            setStyle({
                color: "black"
            })
        }
    }, [props.currentRating])

    return (
        <button style={style} onClick={() => props.onClick(props.rating)}> {props.rating} </button>
    )
}
